Scripts.OnLoad:
TMW:CInit(self, {
	doCleanString = true,
})

self:SetTexts(TMW.L["ANIM_TEX"], TMW.L["ANIM_TEX_DESC"])
self:SetSetting("Image")
self:SetColumnPadding(4, 7)

self:CScriptAdd("ModifySettingValueRequested", function(self, value)
	return value:gsub([[\\]], [[\]])
end)

TMW.SUG:EnableEditBox(self, "texture", true)
